function Logoiframe() {
  document.querySelectorAll(".divSvg iframe").forEach((e) => {
    const ifr = e.contentDocument || e.contentWindow.document;
    ifr.querySelector("svg").style.width = "50px";
    ifr.querySelector("svg").style.height = "60px";
  });
}

